//Version Check Update
function version_update_check() {
define('REMOTE_VERSION', 'http://visitorsignin.ignitepros.com/update/version.txt');
$version_check_now = @file_get_contents(REMOTE_VERSION);

if($version_check_now === false) {
	return "<strong>Failed to check for version updates.<br />Contact support@ignitepros.com</strong><br />";
	exit();
}

$version_check = REMOTE_VERSION;
$version = VERSION;

if($version != $version_check_now) {
return "<div class='error'> 
There is a update available <strong>$version_check_now</strong>!<br /><br /><a href='https://www.ignitepros.com' target='_blank' class='btn btn-success'>Download Update</a><br />
</div>";
} else {
return "<div class='success'> 
<p> You have the latest version <strong>$version</strong>!</p> 
</div>";
	}
}

